    <script type="text/javascript" language="javascript">
        $(document).ready(function () {
            $('ul#ulParent li:last').css({'background-image': 'none'});
            var str = location.href.toLowerCase();
            $('ul#ulParent li a').each(function () {
                if (str.indexOf(this.href.toLowerCase()) > -1) {
                    $(this).attr("class", "current"); //hightlight parent tab
                }
            });
            if (str.indexOf("Article.aspx".toLowerCase()) > -1) {
                $('ul#ulParent li a.level1').each(function () {

                    var a = $(this).attr("href");
                    if (a.indexOf("\Lecture.aspx") > -1) {

                        $(this).attr("class", "current"); //hightlight parent tab
                    }
                });

            }
            if (str.indexOf("Newsletter.aspx".toLowerCase()) > -1) {
                $('ul#ulParent li a.level1').each(function () {

                    var a = $(this).attr("href");
                    if (a.indexOf("\Category.aspx") > -1) {

                        $(this).attr("class", "current"); //hightlight parent tab
                    }
                });
            }
            $('.detail').hide();

            $('.frameReply').hide();
            $('div.addReply').click(function () {

                $('.frameReply').slideUp('slow');
                if ($(this).next().is(':hidden') == true) {

                    $(this).next().slideDown('slow');
                }

            });

        });
    </script>
